import { Component, OnInit } from '@angular/core';
import { UserService } from 'src/app/services/user.service'; 
import { VideoService } from 'src/app/services/video.service';
import { Router, ActivatedRoute, Params } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  providers: [UserService, VideoService]
})
export class HomeComponent implements OnInit{

  public page_title: string;
  public identity: any;
  public token: any;
  public videos: any[];
  public status: any;
  public page: any;
  public next_page: any;
  public prev_page: any;
  public number_pages: any;

  constructor(
    private _userService: UserService,
    private _videoService: VideoService,
    private _router: Router,
    private _route: ActivatedRoute
  ){
    this.page_title = "Mis videos";
    this.status = '';
    this.videos = [];
    this.page = '';
    this.next_page = '';
    this.prev_page = '';
    this.number_pages = '';
  }

  ngOnInit(){
    this.loadUser();

    this._route.params.subscribe(params=>{
        var page = +params['page'];

        if(!page){
            page = 1;
            this.prev_page = 1;
            this.next_page = 2;
        }

        this.getVideos(page);

    });

  }

  ngDoCheck(): void {
    console.log('HomeComponent Cargado!');
    this.loadUser();
  }

  loadUser(){
    this.identity = this._userService.getIdentity();
    this.token = this._userService.getToken();
  }

  getVideos(page:any=null){
      this._videoService.getVideos(this.token, page).subscribe(
        response =>{
          this.videos = response.pagination;   

          let number_pages = [];
          for(let i=1; i<=response.total_pages; i++){
              number_pages.push(i);
          }
          this.number_pages = number_pages;

          if(page >= 2){
            this.prev_page = page-1;
          }else{
            this.prev_page = 1;
          }

          if(page < response.total_pages){
            this.next_page = page + 1;
          }else{
            this.next_page = response.total_pages;
          }



        },
        error =>{
          this.status = 'error';
          console.log(error);
        }
      );
  }

  getThumb(url:any, size:any) {
    var video, results, thumburl;

     if (url === null) {
         return '';
     }

     results = url.match('[\\?&]v=([^&#]*)');
     video   = (results === null) ? url : results[1];

     if(size != null) {
         thumburl = 'http://img.youtube.com/vi/' + video + '/'+ size +'.jpg';
     }else{
         thumburl = 'http://img.youtube.com/vi/' + video + '/mqdefault.jpg';
     }

      return thumburl;

  }

  deleteVideo(id:any){
      this._videoService.delete(this.token, id).subscribe(
          response =>{
              this.getVideos();
          },
          error =>{
              this.status = 'error';
              console.log(error);
          }
      );    
  }


}
<div class="col-md-12 mt-3" *ngIf="identity.sub">
    <h1>{{page_title}}</h1>
    <hr>

    <div class="card-columns">
        <article class="card w-100" *ngFor="let video of videos">
            <div class="card-header">
                <h3>
                    {{video.title}}
                </h3>
            </div>
            <div class="card-body">
                <!--MINIATURA-->
                <div class="video-image w-100" *ngIf="video.url">
                    <img src="{{getThumb(video.url, null)}}" class="w-100">
                </div>
                <div class="video-image w-100" *ngIf="!video.url">
                    <img src="assets/images/video-default.png" class="w-100">
                </div>
                <!--BOTONES-->
                <div class="video-buttons mt-3">
                    <a class="btn btn-success mr-2">Leer</a>
                    <a class="btn btn-warning mr-2">Editar</a>

                    					<!-- Button to Open the Modal -->
					<button type="button" class="btn btn-sm btn-danger mt-2" data-toggle="modal" [attr.data-target]="'#borrar'+video._id">
                        Borrar
                    </button>

                    <!-- The Modal -->
                    <div class="modal" id="myModal{{video._id}}">
                            <div class="modal-dialog">
                                    <div class="modal-content">
                                        <!-- Modal Header -->
                                        <div class="modal-header">
                                            <h4 class="modal-title">¿Seguro que quieres borrar el video?</h4>
                                            <button type="button" class="close" data-dismiss="modal">&times;</button>
                                        </div>

                                            <!-- Modal body -->
                                        <div class="modal-body">
                                                Vas a borrar definitivamente el tema {{ video.title }}
                                        </div>

                                        <!-- Modal footer -->
                                        <div class="modal-footer">
                                                <a type="button" class="btn btn-danger" (click)="deleteVideo(video._id)"  data-dismiss="modal">Borrar</a>

                                                <button type="button" class="btn btn-success" data-dismiss="modal">
                                                    Cancelar
                                                </button>
                                        </div>
                                    </div>
                            </div>
                    </div>
                </div>
            </div>
        </article>
    </div>

    <nav aria-label="Page navigation example">
        <ul class="pagination">
          <li class="page-item">
                <a class="page-link" [routerLink]="['/inicio',prev_page]">Previous</a>
          </li>
          <li class="page-item" *ngFor="let num of number_pages">
                <a class="page-link" [routerLink]="['/inicio',num]">{{num}}</a>
          </li>
          <li class="page-item">
                <a class="page-link" [routerLink]="['/inicio',next_page]">Next</a>
          </li>
        </ul>
    </nav>
</div>

<div class="col-md-12 mt-3" *ngIf="!identity.sub">
    <div class="jumbotron">
        <h1 class="display-4">Bienvenido a Favovideos!</h1>
        <p class="lead">Registrate en la web para guardar tus videos favoritos de YOUTUBE y poder verlos cuando quieras.</p>
        <hr class="my-4">
        <p class="lead">
          <a class="btn btn-primary btn-lg" [routerLink]="['/login']" role="button">Identificate</a>
          <a class="btn btn-success btn-lg ml-3" [routerLink]="['/registro']" role="button">Registrate</a>
        </p>
      </div>
</div>
