import { Component, OnInit } from '@angular/core';
import { UserService } from 'src/app/services/user.service'; 
import { VideoService } from 'src/app/services/video.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  providers: [UserService, VideoService]
})
export class HomeComponent implements OnInit{

  public page_title: string;
  public identity: any;
  public token: any;
  public videos: any[] = [];
  public status: any;

  constructor(
    private _userService: UserService,
    private _videoService: VideoService
  ){
    this.page_title = "Mis videos";
    this.status = '';
  }

  ngOnInit(){
    this.loadUser(); 
  }

  ngDoCheck(): void {
    this.loadUser();        
  }

  loadUser(){
    this.identity = this._userService.getIdentity();
    this.token = this._userService.getToken();
  }

  getVideos(){
      this._videoService.getVideos(this.token).subscribe(
        response =>{
          this.videos = response.videos;   
          console.log(response.videos);         
        },
        error =>{
          this.status = 'error';
          console.log(error);
        }
      );
  }


}
<div class="col-md-12 mt-3" *ngIf="identity.sub">
    <h1>{{page_title}}</h1>
    <hr>

    <div class="card-columns">
        <article class="card w-100" *ngFor="let video of videos">
            <div class="card-header">
                <h3>
                    {{video.title}}
                </h3>
            </div>
        </article>
    </div>
</div>

<div class="col-md-12 mt-3" *ngIf="!identity.sub">
    <div class="jumbotron">
        <h1 class="display-4">Bienvenido a Favovideos!</h1>
        <p class="lead">Registrate en la web para guardar tus videos favoritos de YOUTUBE y poder verlos cuando quieras.</p>
        <hr class="my-4">
        <p class="lead">
          <a class="btn btn-primary btn-lg" [routerLink]="['/login']" role="button">Identificate</a>
          <a class="btn btn-success btn-lg ml-3" [routerLink]="['/registro']" role="button">Registrate</a>
        </p>
      </div>
</div>
