import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute, Params } from '@angular/router';
import { UserService } from 'src/app/services/user.service';
import { Video } from 'src/app/models/video';
import { global } from 'src/app/services/global';

@Component({
  selector: 'app-video-new',
  templateUrl: './video-new.component.html',
  styleUrls: ['./video-new.component.css'],
  providers: [UserService]
})
export class VideoNewComponent implements OnInit{

  public page_title: string;
  public identity: any;
  public token: any;
  public video: Video;
  public status: string;

  constructor(
    private _route: ActivatedRoute,
    private _router: Router,
    private _userService: UserService
  ){
    this.page_title = 'Guardar video favorito';
    this.status = 'error';
    this.identity = this._userService.getIdentity();
    this.token = this._userService.getToken();
    this.video = new Video(1, this.identity.sub, '', '', '', '', '', '');
  }

  ngOnInit(): void {
    console.log('VideoNewComponent Cargado!');
  }

  onSubmit(form:any){

  }

}
<div class="col-md-12 mt-3">
    <h1>{{page_title}}</h1>
    <hr>

    <div class="alert alert-success col-md-5 mt-3" *ngIf="status == 'success'">
		Video guardado correctamente
	</div>

	<div class="alert alert-danger col-md-5 mt-3" *ngIf="status == 'error'">
		No se ha guardado el video
	</div>

    <form class="col-md-5 ml-0 pl-0" #videoNew="ngForm" (ngSubmit)="onSubmit(videoNew)">

        <div class="form-group">
			<label for="name">Titulo</label>
			<input type="text" name="title" class="form-control" #name="ngModel" [(ngModel)]="video.title" required pattern="[a-zA-ZÀ-ÿ ]+" />

			<small *ngIf="!video.title && video.title" class="invalid-feedback d-block">
				El titulo no es válido
			</small>
		</div>

		<div class="form-group">
			<label for="surname">Apellidos</label>
			<input type="text" name="surname" class="form-control" #surname="ngModel" [(ngModel)]="user.surname" required pattern="[a-zA-ZÀ-ÿ ]+" />

			<small *ngIf="!surname.valid && surname.touched" class="invalid-feedback d-block">
				Los apellidos no son válidos
			</small>
		</div>

		<div class="form-group">
			<label for="email">Correo electrónico</label>
			<input type="email" name="email" class="form-control" #email="ngModel" [(ngModel)]="user.email" required pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$" />

			<small *ngIf="!email.valid && email.touched" class="invalid-feedback d-block">
				El email no es valido
			</small>
		</div>

        <input type="submit" value="Actualizar" class="btn btn-success" [disabled]="videoNew.invalid" />

    </form>
</div>
